// --------------------------------Functions -----------------------
void MoveToRam(uint32_t x, uint32_t y, const unsigned short bitmap_origin[], volatile unsigned short bitmap_destination[]){
	for (int i = 0; i < y; i ++){
		for (int j = 0; j < x; j ++){
			bitmap_destination[i * x + j] = bitmap_origin[((y-1) - i) * x + j];
		}
	}
}

void Recolor(uint32_t x, uint32_t y, const unsigned short bitmap_origin[], volatile unsigned short bitmap_destination[], uint16_t Orig_color, uint16_t new_color){
	for (int i = 0; i < y; i ++){
		for (int j = 0; j < x; j ++){
			if ((bitmap_origin[((y-1) - i) * x + j] == 0x03FF)){
			bitmap_destination[i * x + j] = (bitmap_origin[((y-1) - i) * x + j] / Orig_color) * new_color;
			}
		}
	}
}

void Recolor_R(uint32_t x, uint32_t y, const unsigned short bitmap_origin[], volatile unsigned short bitmap_destination[], uint16_t Orig_color, uint16_t new_color){
	for (int i = 0; i < y; i ++){
		for (int j = 0; j < x; j ++){
			if ((bitmap_origin[((y-1) - i) * x + j] == 0x03FF)){
			bitmap_destination[i * x + j] = (bitmap_origin[((y-1) - i) * x + j] / Orig_color) * new_color;
			}
		}
	}
}

void Recolor_L(uint32_t x, uint32_t y, const unsigned short bitmap_origin[], volatile unsigned short bitmap_destination[], uint16_t Orig_color, uint16_t new_color){
	for (int i = 0; i < x * y -1; i ++){
		bitmap_destination[i] = (bitmap_origin[(x*y)-i] / Orig_color) * new_color;
	}
}

void MoveToRam_Down_Sword(){
	for (int i = 0; i < 27; i++){
		HisSwordDown[i] = UpSword[i];
		SwordDown[i] = UpSword[i];
	}
}

void MoveToRam_Left(unsigned short new_color){
	for (int i = 0; i<696; i++)
		{
			RestBlue[i] = WalkLegsClose[695 - i]/0x03FF * new_color;
			RestBlue2[i] = WalkLegsOpen[695 - i]/0x03FF * new_color;
			HePullUp[i] = LungeUp[695 - i]/0x03FF * new_color;
			if(HighLunge[695-i] == 0x03FF)
			HeLunge[i] = HighLunge[695 - i]/0x03FF * new_color;
			else
			HeLunge[i] = HighLunge[695 - i];
			if(LowLunge[695-i] == 0x03FF)
			HeLungeLow[i] = LowLunge[695 - i]/0x03FF * new_color;
			else
			HeLungeLow[i] = 0;	
		}
}
